module.exports.add = function(x,y){
    return x + y;
}

module.exports.squareadd = function(x,y){
    return x*x + y*y;
}
module.exports.cubeadd = function(x,y){
    return x*x*x + y*y*y;
}
module.exports.add5 = function(x,y){
    return x*x*x*x*x + y*y*y*y*y;
}

module.exports.add4 = function(x,y){
    return x*x*x*x + y*y*y*y;
}
module.exports.add6 = function(x,y){
    return x*x*x*x*x*x + y*y*y*y*y*y;
}

module.exports.add7 = function(x,y){
    return x*x*x*x*x*x*x + y*y*y*y*y*y*y;
}
